package EjemplosClase;

public class BuclesFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Bucles definidos
		// Ejemplo for
		for (int i=0;i<10;i++) {
		    System.out.println(i);
		}

		// Con paso 2
		for (int i=0;i<10;i+=2) {
		    System.out.println(i);
		}


		// descedente
		for (int i=10;i>0;i--) {
		    System.out.println(i);
		}

		// Descedente con paso 3
		for (int i=10;i>0;i-=3) {
		    System.out.println(i);
		}
		
		
	}

}
