package EjemplosClase;

public class BuclesWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Bucles indefinidos
		// Ejemplo while
		int i = 0;
		while (i < 10) {
		    System.out.println(i);
		    i++;
		}

		// Ejemplo do-while
		int j = 0;
		do {
			System.out.println(j);
		    j++;
		} while (j<10);
	}

}
